package com.bdoemu.core.network.sendable;

import com.bdoemu.commons.network.SendByteBuffer;
import com.bdoemu.commons.network.SendablePacket;
import com.bdoemu.commons.utils.HexUtils;
import com.bdoemu.core.network.GameClient;

public class SMXigncodeSecurityData extends SendablePacket<GameClient> {
    private static byte[] data1 = HexUtils.hex2Byte("");
    private static byte[] data2 = HexUtils.hex2Byte("");
    private static byte[] data3 = HexUtils.hex2Byte("");
    private static byte[] data4 = HexUtils.hex2Byte("");
    private static byte[] data5 = HexUtils.hex2Byte("");
    private final int dataNumber;

    public SMXigncodeSecurityData(final int dataNumber) {
        this.dataNumber = dataNumber;
    }

    protected void writeBody(final SendByteBuffer buffer) {
        switch (this.dataNumber) {
            case 1:
                buffer.writeB(SMXigncodeSecurityData.data1);
                break;
            case 2:
                buffer.writeB(SMXigncodeSecurityData.data2);
                break;
            case 3:
                buffer.writeB(SMXigncodeSecurityData.data3);
                break;
            case 4:
                buffer.writeB(SMXigncodeSecurityData.data4);
                break;
            case 5:
                buffer.writeB(SMXigncodeSecurityData.data5);
                break;
        }
    }
}
